const navToggle=document.querySelector(".nav-toggle"),body=document.body;navToggle.addEventListener("click",()=>{body.classList.toggle("nav-open")});const logo=document.querySelector(".logo");logo.addEventListener("click",e=>{e.preventDefault(),window.scrollTo({top:0,behavior:"smooth"})});const bioToggle=document.querySelector(".bio-toggle"),bioContent=document.querySelector(".bio-content");bioToggle.addEventListener("click",()=>{const e="true"===bioToggle.getAttribute("aria-expanded");bioToggle.setAttribute("aria-expanded",String(!e)),bioContent.hidden=e});const navLinks=document.querySelectorAll("nav a"),sections=[...document.querySelectorAll("section")];window.addEventListener("scroll",()=>{const e=window.scrollY+window.innerHeight/2;sections.forEach(o=>{e>=o.offsetTop&&e<o.offsetTop+o.offsetHeight&&navLinks.forEach(e=>e.classList.toggle("active",e.getAttribute("href")===`#${o.id}`))})});const videoTriggers=document.querySelectorAll(".video-thumb"),modal=document.querySelector(".video-modal"),modalVideo=modal.querySelector("video"),modalClose=modal.querySelector(".close-modal");videoTriggers.forEach(e=>{e.addEventListener("click",()=>{modalVideo.src=e.dataset.videoSrc,modal.classList.add("open"),modalVideo.play()})}),modalClose.addEventListener("click",()=>{modal.classList.remove("open"),modalVideo.pause(),modalVideo.src=""});const form=document.getElementById("contact-form"),successMsg=document.getElementById("success-msg");form.addEventListener("submit",e=>{e.preventDefault();const o=new FormData(form);fetch(form.action,{method:form.method,body:o}).then(e=>{e.ok?(form.hidden=!0,successMsg.hidden=!1):alert("Submission failed. Please try again.")}).catch(()=>{alert("Network error. Please try later.")})});